refentry.refsect1:
&reftitle.description;


 http:// ve https:// aktarımları için
 bağlam seçenekleri.

  &reftitle.examples;



    - Bir sayfayı çekip veriyi POST ile göndermek


<?php

$postverisi = http_build_query(
    array(
        'var1' => 'biraz içerik',
        'var2' => 'eh işte'
    )
);

$seçenekler = array('http' =>
    array(
        'method'  => 'POST',
        'header'  => 'Content-type: application/x-www-form-urlencoded',
        'content' => $postverisi
    )
);

$bağlam = stream_context_create($seçenekler);

$sonuç = file_get_contents('http://mesela.dom/submit.php', false, $bağlam);

?>







    - Başlıklar ve içerik alınırken yönlendirmeleri yoksaymak 


<?php

$url = "http://www.example.org/header.php";

$opts = array('http' =>
    array(
        'method' => 'GET',
        'max_redirects' => '0',
        'ignore_errors' => '1'
    )
);

$context = stream_context_create($opts);
$stream = fopen($url, 'r', false, $context);

// meta veri ve başlıklar
var_dump(stream_get_meta_data($stream));

// esas veri
var_dump(stream_get_contents($stream));
fclose($stream);
?>
