#0 {main}
  thrown in %s  on line 27

sect2.example:
    - Nesne Atama


<?php
$örnek = new BasitSınıf();

$atama     =  $örnek;
$gönderim  =& $örnek;

$örnek->var = '$atama bu değere sahip olacak';

$örnek = null; // $örnek ve $gönderim null olur

var_dump($örnek);
var_dump($gönderim);
var_dump($atama);
?>


    &example.outputs;


NULL
NULL
